import json
from typing import Any, List

from langflow.custom import Component
from langflow.io import (
    BoolInput, HandleInput, MessageTextInput, MultilineInput,
    Output, TabInput
)
from langflow.schema import Data, DataFrame
from langflow.schema.message import Message


class ParserComponent(Component):
    display_name = "Parser"
    name = "ParserComponent"
    icon = "braces"
    description = (
        "Format Data / DataFrame into text with a template, or stringify. "
        "Adds first_image_tag automatically when images are present."
    )

    # ───────── inputs ─────────
    inputs = [
        TabInput(
            name="mode", display_name="Mode",
            options=["Parser", "Stringify"], value="Parser",
            real_time_refresh=True,
        ),
        MultilineInput(
            name="pattern", display_name="Template",
            value="{text}\n\n{first_image_tag}",
            info="Use {text}, {first_image_tag}, or any other keys in the row/Data.",
            dynamic=True, required=True,
        ),
        HandleInput(
            name="input_data", display_name="Data or DataFrame",
            input_types=["Data", "DataFrame"], required=True,
        ),
        MessageTextInput(
            name="sep", display_name="Separator",
            value="\n", advanced=True,
        ),
    ]

    outputs = [
        Output(
            name="parsed_text", display_name="Parsed Text",
            method="parse_combined_text"
        )
    ]

    # ───────── helpers ─────────
    def _to_list(self) -> List[Data]:
        inp = self.input_data
        if isinstance(inp, DataFrame):
            # Set default text_key if present
            if hasattr(inp, "text_key") and "text" in inp.columns:
                inp.text_key = "text"
            # If not, just convert as-is
            return [row for row in inp.to_data_list()]
        if isinstance(inp, Data):
            return [inp]
        if isinstance(inp, list):
            return [d for d in inp if isinstance(d, Data)]
        raise ValueError("Unsupported input type.")

    @staticmethod
    def _img_tag(b64: str) -> str:
        return (
            f'<img src="data:image/png;base64,{b64}" '
            'style="max-width:250px;border:1px solid #ccc;" />'
        )

    # ───────── core ─────────
    def parse_combined_text(self) -> Message:
        if self.mode == "Stringify":
            return Message(text=self._safe_convert(self.input_data))

        lines = []
        for d in self._to_list():
            first_img = ""
            if "images" in d.metadata and d.metadata["images"]:
                first_img = self._img_tag(d.metadata["images"][0])

            # Try to get the main text, robustly!
            main_text = getattr(d, "text", None)
            if not main_text:
                # Try common alternatives in metadata
                for alt in ["content", "body", "paragraph"]:
                    main_text = d.metadata.get(alt)
                    if main_text:
                        break
            if not main_text:
                # Fallback: use the first string value in metadata
                for val in d.metadata.values():
                    if isinstance(val, str) and val.strip():
                        main_text = val
                        break
            if not main_text:
                # Still nothing: error, show available keys
                raise ValueError(
                    f"No 'text' key found in Data/DataFrame or metadata (available keys: {list(d.metadata.keys())})"
                )

            payload = {
                **d.metadata,    # filename, images, etc.
                "text": main_text,
                "first_image_tag": first_img,
            }
            lines.append(self.pattern.format(**payload))

        combined = self.sep.join(lines)
        self.status = f"Rendered {len(lines)} block(s)"
        return Message(text=combined)

    # stringify helper
    def _safe_convert(self, obj: Any) -> str:
        if isinstance(obj, (Data, Message)):
            return json.dumps(obj.data if isinstance(obj, Data) else obj.get_text())
        if isinstance(obj, DataFrame):
            return obj.to_markdown(index=False)
        return str(obj)
