from pymongo.mongo_client import MongoClient
from pymongo.server_api import ServerApi
from pymongo.operations import SearchIndexModel
from langflow.custom import Component
from langflow.io import MessageTextInput, Output
from langflow.schema import Message


class CreateCosineVectorIndex(Component):
    """
    Connect to MongoDB Atlas, ping the server, ensure the target collection exists,
    and create a cosine-vector search index on its `embedding` field.
    Returns a Message with success/failure text.
    """

    display_name = "Create Cosine Vector Index"
    name = "CreateCosineVectorIndex"
    icon = "database"
    description = "Creates a cosine-vector search index on a specified MongoDB collection."
    documentation = "https://docs.mongodb.com/manual/core/search-index-vector/"

    inputs = [
        MessageTextInput(
            name="collection_name",
            display_name="Collection Name",
            info="Name of the MongoDB collection to index",
            value="myCollection",
            tool_mode=True,
        ),
    ]

    outputs = [
        Output(display_name="Output", name="output", method="build_output"),
    ]

    def build_output(self) -> Message:
        # unwrap MessageTextInput -> str
        coll_name = (
            self.collection_name.content
            if hasattr(self.collection_name, "content")
            else self.collection_name
        ).strip()
        idx_name = "cosine_vector_index"

        uri = (
            "mongodb+srv://beprith99:jMRmGQtfiA5jPucu"
            "@ot-service-bot.9cpfrsr.mongodb.net/"
            "?retryWrites=true&w=majority&appName=OT-Service-Bot"
        )

        client = MongoClient(uri, server_api=ServerApi("1"))
        try:
            # 1) Ping
            try:
                client.admin.command("ping")
            except Exception as ping_err:
                return Message(role="assistant", content=f"❌ Ping failed: {ping_err}")

            db = client["ot-service"]

            # 2) Ensure collection exists
            if coll_name not in db.list_collection_names():
                try:
                    db.create_collection(coll_name)
                except Exception as ce_err:
                    return Message(
                        role="assistant",
                        content=f"❌ Could not create collection `{coll_name}`: {ce_err}",
                    )

            collection = db[coll_name]

            # 3) Define and create the vectorSearch index
            idx_def = SearchIndexModel(
                definition={
                    "fields": [
                        {
                            "type": "vector",
                            "path": "embedding",
                            "numDimensions": 1024,
                            "similarity": "cosine",
                        }
                    ]
                },
                name=idx_name,
                type="vectorSearch",
            )

            try:
                result = collection.create_search_index(idx_def)
                return Message(
                    role="assistant",
                    content=f"✅ Index `{idx_name}` created on `{coll_name}`: {result}",
                )
            except Exception as idx_err:
                return Message(
                    role="assistant",
                    content=f"❌ Failed to create index `{idx_name}`: {idx_err}",
                )

        finally:
            client.close()

from langchain_text_splitters import CharacterTextSplitter

from langflow.custom import Component
from langflow.io import DropdownInput, HandleInput, IntInput, MessageTextInput, Output
from langflow.schema import Data, DataFrame
from langflow.utils.util import unescape_string


class SplitTextComponent(Component):
    display_name: str = "Split Text"
    description: str = "Split text into chunks based on specified criteria."
    icon = "scissors-line-dashed"
    name = "SplitText"

    inputs = [
        HandleInput(
            name="data_inputs",
            display_name="Data or DataFrame",
            info="The data with texts to split in chunks.",
            input_types=["Data", "DataFrame"],
            required=True,
        ),
        IntInput(
            name="chunk_overlap",
            display_name="Chunk Overlap",
            info="Number of characters to overlap between chunks.",
            value=200,
        ),
        IntInput(
            name="chunk_size",
            display_name="Chunk Size",
            info=(
                "The maximum length of each chunk. Text is first split by separator, "
                "then chunks are merged up to this size. "
                "Individual splits larger than this won't be further divided."
            ),
            value=1000,
        ),
        MessageTextInput(
            name="separator",
            display_name="Separator",
            info=(
                "The character to split on. Use \\n for newline. "
                "Examples: \\n\\n for paragraphs, \\n for lines, . for sentences"
            ),
            value="\n",
        ),
        MessageTextInput(
            name="text_key",
            display_name="Text Key",
            info="The key to use for the text column.",
            value="text",
            advanced=True,
        ),
        DropdownInput(
            name="keep_separator",
            display_name="Keep Separator",
            info="Whether to keep the separator in the output chunks and where to place it.",
            options=["False", "True", "Start", "End"],
            value="False",
            advanced=True,
        ),
    ]

    outputs = [
        Output(display_name="Chunks", name="chunks", method="split_text"),
        Output(display_name="DataFrame", name="dataframe", method="as_dataframe"),
    ]

    def _docs_to_data(self, docs) -> list[Data]:
        return [Data(text=doc.page_content) for doc in docs]

    def _fix_separator(self, separator: str) -> str:
        """Fix common separator issues and convert to proper format."""
        if separator == "/n":
            return "\n"
        if separator == "/t":
            return "\t"
        return separator

    def split_text_base(self):
        separator = self._fix_separator(self.separator)
        separator = unescape_string(separator)

        if isinstance(self.data_inputs, DataFrame):
            if not len(self.data_inputs):
                msg = "DataFrame is empty"
                raise TypeError(msg)

            self.data_inputs.text_key = self.text_key
            try:
                documents = self.data_inputs.to_lc_documents()
            except Exception as e:
                msg = f"Error converting DataFrame to documents: {e}"
                raise TypeError(msg) from e
        else:
            if not self.data_inputs:
                msg = "No data inputs provided"
                raise TypeError(msg)

            documents = []
            if isinstance(self.data_inputs, Data):
                self.data_inputs.text_key = self.text_key
                documents = [self.data_inputs.to_lc_document()]
            else:
                try:
                    documents = [input_.to_lc_document() for input_ in self.data_inputs if isinstance(input_, Data)]
                    if not documents:
                        msg = f"No valid Data inputs found in {type(self.data_inputs)}"
                        raise TypeError(msg)
                except AttributeError as e:
                    msg = f"Invalid input type in collection: {e}"
                    raise TypeError(msg) from e
        try:
            # Convert string 'False'/'True' to boolean
            keep_sep = self.keep_separator
            if isinstance(keep_sep, str):
                if keep_sep.lower() == "false":
                    keep_sep = False
                elif keep_sep.lower() == "true":
                    keep_sep = True
                # 'start' and 'end' are kept as strings

            splitter = CharacterTextSplitter(
                chunk_overlap=self.chunk_overlap,
                chunk_size=self.chunk_size,
                separator=separator,
                keep_separator=keep_sep,
            )
            return splitter.split_documents(documents)
        except Exception as e:
            msg = f"Error splitting text: {e}"
            raise TypeError(msg) from e

    def split_text(self) -> list[Data]:
        return self._docs_to_data(self.split_text_base())

    def as_dataframe(self) -> DataFrame:
        return DataFrame(self.split_text())

